public class Part1 {
    public static void main(String[] args) {
        String[] input = {
                "",
                "",
                "",
                "",
                ""
        };
        int[][] keypad = {{7,8,9}, {4,5,6}, {1,2,3} };  // HACK: this translates to

        int x = 2,  y = 2;
        for (int i=0; i < input.length; i++) {
            for (int j=0; j < input[i].length(); j++) {
                switch (input[i].charAt(j)) {
                    case 'U': y++; y = clamp(y, 0, 2); break;
                    case 'D': y--; y = clamp(y, 0, 2); break;
                    case 'R': x++; x = clamp(x, 0, 2); break;
                    case 'L': x--; x = clamp(x, 0, 2); break;
                }
            }
            System.out.print( keypad[y][x] );
        }
    }

    public static int clamp(int i, int min, int max) {
        return (i < min)? min : (i > max)? max : i;
    }
}
